using Microsoft.EntityFrameworkCore;
using System;
using System.Collections.Generic;
using System.IO;
using System.Text;
using System.Threading.Tasks;
using Xamarin.Essentials;
using Xamarin.Forms;
using XamarinSQLite.Core;
using XamarinSQLite.Models;

namespace XamarinSQLite.Services
{
    public static class DataBaseService
    {
        public static async Task Init()
        {
            string path = await DependencyService.Get<IFileSystem>().GetAbsDbPath();
            var db = new AppDataBaseContext();
            await db.Init(path);
            await db.DisposeAsync();
        }

        public static async Task<User[]> GetUsers()
        {
            var db = new AppDataBaseContext();
            var res = await db.GetUsers();
            await db.DisposeAsync();
            return res;
        }

        public static async Task SaveUser(User user)
        {
            var db = new AppDataBaseContext();
            await db.SaveUser(user);
            await db.DisposeAsync();
        }
    }

    public class AppDataBaseContext : DbContext
    {
        private static string DataBaseDirPath;

        public const string DbName = "mySQLiteDB.db3";
        public DbSet<User> Users { get; set; }

        public AppDataBaseContext()
        {
            // For iOS
            SQLitePCL.Batteries_V2.Init();
        }

        public async Task Init(string dataBaseDirPath)
        {
            DataBaseDirPath = dataBaseDirPath;
            bool isCreated = await Database.EnsureCreatedAsync();

            if (isCreated)
                await InitSeedData();
        }

        public Task<User[]> GetUsers()
        {
            var res = Users.ToArrayAsync();
            return res;
        }

        public async Task SaveUser(User user)
        {
            await Users.AddAsync(user);
            await SaveChangesAsync();
        }

        protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)
        {
            string dbPath = Path.Combine(DataBaseDirPath, DbName);
            optionsBuilder.UseSqlite($"Filename={dbPath}");
        }

        private async Task InitSeedData()
        {
            var users = new User[]
            {
                new User
                {
                    FirstName = "Boris",
                    LastName = "Krit",
                    AvatarBase64 = "",
                },
                new User
                {
                    FirstName = "Anna",
                    LastName = "Abraham",
                    AvatarBase64 = "",
                },
                new User
                {
                    FirstName = "Sam",
                    LastName = "Wood",
                    AvatarBase64 = "",
                },
                new User
                {
                    FirstName = "Candie",
                    LastName = "Hopping",
                    AvatarBase64 = "",
                }
            };

            for (int i = 0; i < users.Length; i++)
            {
                var user = users[i];

                var match = await Users.FirstOrDefaultAsync(x => x.Id == i);
                if (match == null)
                    Users.Add(user);
            }

            await SaveChangesAsync();
        }
    }
}
